mapper.select:
SELECT id, type_code AS typeCode, label, value, sort, ext, status, create_time AS createTime, update_time AS updateTime FROM sys_dict_item WHERE 1 = 1
AND type_code = #{q.typeCode}
AND (label LIKE CONCAT('%', #{q.keyword}, '%') OR value LIKE CONCAT('%', #{q.keyword}, '%'))
AND status = #{q.status}
ORDER BY sort ASC, id ASC
